Scripts.OnEditFocusLost:
local slider = self:GetParent( );
local OnValueChangedScript = slider:GetScript( "OnValueChanged" );
if OnValueChangedScript then
    OnValueChangedScript( slider, slider:GetValue( ) );
end

Scripts.OnEnterPressed:
                        local slider = self:GetParent( );
                        local value = tonumber(self:GetText( ));
                        local min, max = slider:GetMinMaxValues( );

                        
                        if( value and value >= min and value <= max ) then
                            slider:SetValue( value );
                        end
                        self:ClearFocus( );

Scripts.OnEnter:
if ( self:GetText( ) ) then
    GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT", 0, -self:GetHeight( ));
    GameTooltip:SetText(self:GetText( ), nil, nil, nil, nil, 1);
end

Scripts.OnLoad:
self.type = CONTROLTYPE_SLIDER;
local min, max = self:GetMinMaxValues( );
getglobal( self:GetName( ) .. "Low" ):SetText( string.format("%.0f", min) );
getglobal( self:GetName( ) .. "High" ):SetText( string.format("%.0f", max) );
self:SetObeyStepOnDrag( true );

Scripts.OnValueChanged:
self.editBox:SetText( string.format("%.02f", value) );
if self.applyToFrame and self.applyToFrame.scale ~= value then
    self.applyToFrame.scale = value;
    StatusBars2_UpdateFullLayout( );
end

Scripts.OnLoad:
self.type = CONTROLTYPE_SLIDER;
local min, max = self:GetMinMaxValues( );
getglobal( self:GetName( ) .. "Low" ):SetText( string.format("%.0f%%", min) );
getglobal( self:GetName( ) .. "High" ):SetText( string.format("%.0f%%", max) );
self:SetObeyStepOnDrag( true );

Scripts.OnValueChanged:
self.editBox:SetText( string.format("%3d", value) );
local realValue = value * 0.01
if self.applyToFrame then
    self.applyToFrame.alpha = realValue;
    self.applyToFrame:SetAlpha( realValue );
end

Scripts.OnLoad:
UIDropDownMenu_Initialize( self, StatusBars2_TextDisplayOptionsMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");
self.label:SetText("Text Display Options");

UIDropDownMenu_Initialize( self, StatusBars2_FontMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");
self.label:SetText("Text Size");

UIDropDownMenu_Initialize( self, StatusBars2_BarEnabledMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

self.type = CONTROLTYPE_SLIDER;
getglobal( self:GetName( ) .. "Low" ):SetText( "0" );
getglobal( self:GetName( ) .. "High" ):SetText( "100%" );
getglobal( self:GetName( ) .. "Low" ):Hide();
getglobal( self:GetName( ) .. "High" ):Hide();

UIDropDownMenu_Initialize( self, StatusBars2_PercentTextMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

Scripts.OnTextChanged:
                        local addButton = self:GetParent( ).addEntryButton;

                        if( self:GetText( ) == "" ) then
                            addButton:Disable( );
                        else
                            addButton:Enable( );
                        end

Scripts.OnLoad:
getglobal( self:GetName( ) .. "Text" ):SetText( "Only show auras listed" );
getglobal( self:GetName( ) .. "Text" ):ClearAllPoints( );
getglobal( self:GetName( ) .. "Text" ):SetPoint("RIGHT", self, "LEFT");
getglobal( self:GetName( ) .. "Text" ):SetJustifyH("RIGHT");

Scripts.OnEnter:
if ( self:GetText( ) ) then
    GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT", 0, -self:GetHeight( ));
    GameTooltip:SetText(self:GetText( ), nil, nil, nil, nil, 1);
end

Scripts.OnLoad:
StatusBars2_Config_BarSelectLabel:ClearAllPoints( );
StatusBars2_Config_BarSelectLabel:SetPoint("RIGHT", self, "LEFT", 10, 2);
UIDropDownMenu_Initialize( self, StatusBars2Config_BarSelect_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");
